INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(1, 1, 'Features', 'Product Dimensions: 8.0 x 7.5 x 6.5 inches ; 1.5 pounds ' + CHAR(13) + CHAR(10) + 'Shipping Weight: 3.1 pounds. (View shipping rates and policies) ' + CHAR(13) + CHAR(10) + 'Shipping: Currently, item can be shipped only within the U.S. ' + CHAR(13) + CHAR(10) + 'ASIN: B000A7XSSY ' + CHAR(13) + CHAR(10) + 'Item model number: DMC-FZ30K ', 1, 5)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(2, 2, 'Features', '<b>From the Manufacturer</b>' + CHAR(13) + CHAR(10) + 'The DMC-FZ30''s Leica DC Vario-Elmarit lens features 14 elements in 8 groups, including 3 aspherical lenses and an ED (extra-low dispersion) lens which helps to suppress the color bleeding that often occurs in telephoto shots, plus a 12x optical zoom (35mm equivalent: 35 to 420mm). This combines with the large, 1/1.8-inch diagonal 8-megapixel CCD to give you enormous expressive power. The new Extended Optical Zoom, which minimizes the image degradation that normally occurs with digital zooming, gives you up to 19.1x power in 3-megapixel photos, and up to 15x power in 5-megapixel photos. Plus, the 12x optical zoom can be used together with the 4x digital zoom to produce a maximum of 48x zooming power.' + CHAR(13) + CHAR(10) + '' + CHAR(13) + CHAR(10) + '<b>Accurate, Flexible Focusing--Both Auto and Manual</b>' + CHAR(13) + CHAR(10) + 'In addition to five auto focus modes (1-point, 3-point high-speed, 9-point, spot AF, and 1-point high-speed AF with reduced focusing speed), the FZ30 features a ring that lets you manually adjust the focus from 0.98 feet (0.3 meters) to infinity. It also has Manual Focus Assist, a function that shows an enlarged image of the subject''s center in the viewfinder. The One-Shot AF function in manual focus mode lets you focus automatically for a single shot by pressing the "FOCUS" button. The Continuous AF function keeps moving subjects in sharp focus, and the AF Assist Lamp helps in dim lighting. This all adds up to easy, accurate focusing.' + CHAR(13) + CHAR(10) + '' + CHAR(13) + CHAR(10) + '<b>Manual Focus and Zoom Rings with Intuitive Control</b>' + CHAR(13) + CHAR(10) + 'The manual focus ring lets you focus on whatever part of your subject you wish, and the zoom ring lets you zoom in and out easily and accurately as you compose the perfect shot. They''re both fast, so you can capture moving subjects in your composition.', 0, 4)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(3, 3, 'Features', 'Product Dimensions: 1.0 pounds ' + CHAR(13) + CHAR(10) + 'Shipping Weight: 2.0 pounds. (View shipping rates and policies) ' + CHAR(13) + CHAR(10) + 'Shipping: Currently, item can be shipped only within the U.S. ' + CHAR(13) + CHAR(10) + 'ASIN: B0009N4OPK ' + CHAR(13) + CHAR(10) + 'Item model number: 800 Digital ', 1, 3)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(4, 4, 'Features', 'Transfers photos via USB; compatible with Windows and Mac operating systems; rechargeable Li-ion battery ' + CHAR(13) + CHAR(10) + '5.0-megapixel sensor captures enough detail for photo-quality 13 x 17-inch prints ' + CHAR(13) + CHAR(10) + '3x optical zoom, 5x digital zoom; safety flash for enhanced image clarity and color ' + CHAR(13) + CHAR(10) + '2.5-inch LCD screen and high quality MPEG4 movie recording ' + CHAR(13) + CHAR(10) + 'Store images on 50 MB of available internal flash memory or with SD/MMC memory card slot ', 1, 2)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(5, 5, 'Features', 'Built-in HD/Digital tuner (ATSC), 181-channel tuner (NTSC), and Digital Cable Ready; 16:9 widescreen picture aspect ratio ' + CHAR(13) + CHAR(10) + 'Includes 3 composite, 3 S-Video, 2 component, 1 DVI, 1 HDMI, 1 PC (D-Sub), 2 Firewire, 1 Monitor, and 2 RF inputs ' + CHAR(13) + CHAR(10) + 'Two stereo speakers, 15 watts apiece (30 watts total) ' + CHAR(13) + CHAR(10) + '50-inch projection HD-ready DLP TV; measures 46.5 x 34.3x14.0 inches (WxHxD) ' + CHAR(13) + CHAR(10) + 'Digital Light Processing technology features better color uniformity over time--no burn-in, screen aging ' + CHAR(13) + CHAR(10) + '', 1, 1)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(6, 6, 'Features', 'Inputs: 3 composite, 3 component, 1 S-Video, 1 RF, 1 HDMI ' + CHAR(13) + CHAR(10) + 'Two stereo speakers, 12 watts apiece (24 watts total) ' + CHAR(13) + CHAR(10) + '50-inch rear-projection LCD TV; measures 46.6 x 32.6 x 16.1 inches (WxHxD) ' + CHAR(13) + CHAR(10) + 'Built-in HD/Digital tuner (ATSC), 181-channel tuner (NTSC); Digital Cable ready ' + CHAR(13) + CHAR(10) + 'Front A/V input for easily hooking up a DVD player or video game console ', 1, 1)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(7, 14, 'Features', '56 Widescreen using Texas Instruments'' latest generation DLP (Digital Light Processing) TV technology with 16 - 9 aspect ratio and up to 10,000 - 1 contrast ratio ' + CHAR(13) + CHAR(10) + 'Digital Cable Ready (DCR) with CableCARD slot ' + CHAR(13) + CHAR(10) + 'Built-In Analog/Digital Tuner (NTSC/ATSC) - You can pick-up free over-the-air HDTV broadcasts using the ATSC tuner and an optional HDTV antenna ', 1, 1)
INSERT INTO  dbo.[CSK_Store_ProductDescriptor] ([descriptorID],[productID],[title],[descriptor],[isBulletedList],[listOrder]) VALUES(8, 15, 'Features', 'Runs for about 2 hours on 4 AA batteries (and can also be powered by an optional AC adapter) ' + CHAR(13) + CHAR(10) + '4-inch antiglare, active-matrix TFT LCD screen with 405 x 305 resolution; 5.75 x 5 x 1.5 inches (W x H x D) ' + CHAR(13) + CHAR(10) + 'Incredibly portable--approximately the size of double CD ' + CHAR(13) + CHAR(10) + 'Single speaker, monaural earphone jack for private listening ' + CHAR(13) + CHAR(10) + 'Includes built-in rod antenna and receives VHF channels 2 to 13 and UHF channels 14 to 69 ' + CHAR(13) + CHAR(10) + '', 1, 1)
